mapper.select:
SELECT MNO, MNAME, MID, MPW, MEMAIL, MPHONE, MGRADE, MREGDATE
  FROM MEMBER
 WHERE MID = #{mId}
   AND MPW = #{mPw}

SELECT COUNT(MNO)
  FROM MEMBER
 WHERE MID = #{mId}

SELECT COUNT(MEMAIL)
  FROM MEMBER
 WHERE MEMAIL = #{mEmail}

mapper.update:
UPDATE MEMBER
   SET MPW = #{mPw}
 WHERE MNO = #{mNo}

UPDATE MEMBER
   SET MNAME = #{mName}
     , MEMAIL = #{mEmail}
 WHERE MNO = #{mNo}
